import React from 'react'
import './Contact.css'
import emailjs from '@emailjs/browser';
const Contact = () => {

    const form = useRef();

    const sendEmail = (e) => {
      e.preventDefault();

      emailjs.sendForm('service_lycmab6', 'template_806vdxm', form.current, 'YOUR_PUBLIC_KEY')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
    };

  return (
    <div className="contact-form">
        <div className="c-left">
        <div className="awesome">
            <span>Get in touch</span>
            <span>Contact me</span>
            {/* <span>lorem epsil <br/>llllllll</span> */}
            <div className='blur s-blur1' style={{background:'#ABF1FF94'}}></div>
            <div className='blur s-blur2' style={{background:'var(--purple)'}}></div>
        </div>
        </div>
        <div className="c-right">
            <form >
                <input type="text" name='user-name' className='user' placeholder='Name' />
                <input type="email" name='user-email' className='user' placeholder='Email' />
                <textarea name="message" className='user' placeholder='Message'></textarea>
                <input type="submit" value="Send" className='button' />

            </form>
        </div>
    </div>
  )
}

export default Contact
